import React, { Component } from "react";
import Card from "react-bootstrap/Card"
import Button from "react-bootstrap/Button"

// The ...props means, spread all of the passed props onto this element
// That way we don't have to define them all individually
class DeleteBtn extends Component {

    cardstyle = {
        width: '125px',
        height: '200px',
        margin: '10px'
    };

    buttonstyle = {
        margin: '10px'
    };

    render() {
        return (
            <div>
                <Card>
                    <Card.Header>
                        {this.props.booktitle}
                        <Button className="float-right" variant="outline-secondary" style={buttonstyle} >Add </Button>
                        <Button href={props.booklink} className="float-right" variant="outline-secondary" style={buttonstyle} >View </Button>
                    </Card.Header>
                    <Card.Body>
                        <Card.Title>Author(s):
                    {this.props.bookauthors.map(author => (
                            " " + author + " "
                        ))}
                        </Card.Title>
                        <Card.Text>
                            <Card.Img className="float-left" variant="top" src={this.props.bookimage} style={cardstyle} />
                            {this.props.bookdescription}
                        </Card.Text>
                    </Card.Body>
                </Card>
                <br />
            </div>
        );
    }
}

export default DeleteBtn;
