import React, { Component } from "react";
import { Col, Row, Container } from "../components/Grid";
import Jumbotron from "../components/Jumbotron";
import InputGroup from "react-bootstrap/InputGroup"
import FormControl from "react-bootstrap/FormControl"
import Button from "react-bootstrap/Button"
import API from "../utils/API";

class SearchBooks extends Component {

  state = {
    books: [],
    inputValue: "",
    value: ""
  };

  handleInputChange = event => {
    // Pull the name and value properties off of the event.target (the element which triggered the event)
    const { name, value } = event.target;

    // Set the state for the appropriate input field
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    alert(`Username: ${this.state.username}\nPassword: ${this.state.password}`);
    this.setState({ username: "", password: "" });
  };

  handleChange(event) {
    this.setState({ value: event.target.value });
  }


  loadBooks = (searchval) => {
    console.log("the thing happened")
    API.bookSearch(searchval)
      .then(res => {
        this.setState({ books: [] })
        console.log(this.state.input)
        for (let i = 0; i < res.data.items.length; i++) {
          this.state.books.push(res.data.items[i])
        }
      })
      .catch(err => console.log(err));
  };

  render() {
    return (
      <Container fluid >
        <Row>
          <Col size="md-12">
            <Jumbotron >
              <div className="h1" >Google Book Search</div>
              <br></br>
              <InputGroup className="mb-3">
                <FormControl
                  placeholder="Enter Book Name"
                  value={this.state.value}
                  onChange={this.handleChange}
                  type="text"
                />
                <InputGroup.Append>
                  <Button type="button" onClick={this.loadBooks} variant="outline-secondary">Search</Button>
                </InputGroup.Append>
              </InputGroup>
            </Jumbotron>
          </Col>
        </Row>
      </Container >
    );
  }
}

export default SearchBooks;
